package io.spring.rabbitmq.rmqorderservice.message;

public class InventoryResponseMessage {

	private String messageId;
	private String message;
	private boolean isFullilable;

	public InventoryResponseMessage() {
		super();
		// TODO Auto-generated constructor stub
	}

	public InventoryResponseMessage(String messageId, String message, boolean isFullilable) {
		super();
		this.messageId = messageId;
		this.message = message;
		this.isFullilable = isFullilable;
	}

	public String getMessageId() {
		return messageId;
	}

	public void setMessageId(String messageId) {
		this.messageId = messageId;
	}

	public String getMessage() {
		return message;
	}

	public void setMessage(String message) {
		this.message = message;
	}

	public boolean isFullilable() {
		return isFullilable;
	}

	public void setFullilable(boolean isFullilable) {
		this.isFullilable = isFullilable;
	}

	@Override
	public String toString() {
		return "InventoryResponseMessage [messageId=" + messageId + ", message=" + message + ", isFullilable="
				+ isFullilable + "]";
	}

}
